import React, { Component } from 'react';
import $ from 'jquery';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
  } from "react-router-dom";

class ArtistList extends React.Component {
  constructor(props){
    super(props);
    this.state = {
     artistList: [],
     artistContent: ''
    };

    this._handleSubmit = this._handleSubmit.bind(this);
    this._handleChange = this._handleChange.bind(this);
    this._handleChangeMsg = this._handleChangeMsg.bind(this);
  }



  _handleChange(e) {
  }

  _handleChangeMsg(e) {
  }

  _handleSubmit(e) {

    e.preventDefault();

    $.ajax({
      url: "http://ws.audioscrobbler.com/2.0/",
      type: 'POST',
      contentType: "application/json; charset=utf-8",
      data: {
        method: 'artist.search',
        artist: $('#artist').val(),
        api_key : '278b674c37ebf25ee302d1bc7edb0b76',
        format : 'json'
      },
      cache: false,
      success: function(data) {
        // Success..
        const artists = data['results']['artistmatches']['artist'];

        this.setState({
            artistList: artists
        });
       $('li').click(function(e) {
        var clickedArtist = $(this).text();
        console.log(clickedArtist);
        $.ajax({
            url: "http://ws.audioscrobbler.com/2.0/",
            type: 'POST',
            contentType: "application/json; charset=utf-8",
            data: {
              method: 'artist.getInfo',
              artist: clickedArtist,
              api_key : '278b674c37ebf25ee302d1bc7edb0b76',
              format : 'json'
            },
            cache: false,
            success: function(data) {
                console.log(data['artist']['bio']['content']);
                this.state = {
                    artistContent: data['artist']['bio']['content']
                };
                window.openAppRoute("/bio");
            }
        });
    });
      }.bind(this),
      // Fail..
      error: function(xhr, status, err) {
        console.log(xhr, status);
        console.log(err);

      }.bind(this)
    });


}

  render() {

    const openAppRoute = (route) => {
        //    Summary:
        //        Helper function for developers to navigation 
        //        to a different route programmatically.
        //hashHistory.push(route);
      };
      window.openAppRoute = openAppRoute;
    function handleCheck(e){
       // console.log(e.target);
    }
    const items = this.state.artistList.map((item, index) =>
    <li key={index}>{item.name}</li>
    );
    return (
        <div>
        <Router>
      <div>
      <div className="artists" id="artistList">
        <div className="filter">
        <form id="searchartists" onSubmit={this._handleSubmit}>
          <input type="text" placeholder="Search Artist" name="artist" id='artist' onChange={this._handleChange} />
          <button type='submit'>Go</button>
        </form>
        </div>

        <ul>{items}</ul>
      </div>

        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/bio">
            <Bio />
          </Route>
        </Switch>
      </div>
    </Router>



      </div>
    )
  }

}
function Home() {
    return (
      <div>
      </div>
    );
  }

  function Bio() {
    return (
      <div>
        <h2>Artist Bio</h2>
      </div>
    );
  }


export default ArtistList;
