import React, { Component } from 'react';
import $ from 'jquery';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
  } from "react-router-dom";
import ReactModal from 'react-modal';

class ArtistList extends React.Component {
  constructor(props){
    super(props);
    this.state = {
     artistList: [],
     artistContent: '',
     showModal: false
    };

    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);

    this._handleSubmit = this._handleSubmit.bind(this);
    this._handleChange = this._handleChange.bind(this);
    this._handleChangeMsg = this._handleChangeMsg.bind(this);
  }
  handleOpenModal () {
    this.setState({ showModal: true });
  }

  handleCloseModal () {
    this.setState({ showModal: false });
  }

  _handleChange(e) {
  }

  _handleChangeMsg(e) {
  }

  _handleSubmit(e) {

    e.preventDefault();

    $.ajax({
      url: "http://ws.audioscrobbler.com/2.0/",
      type: 'POST',
      contentType: "application/json; charset=utf-8",
      data: {
        method: 'artist.search',
        artist: $('#artist').val(),
        api_key : '278b674c37ebf25ee302d1bc7edb0b76',
        format : 'json'
      },
      cache: false,
      success: function(data) {
        // Success..
        const artists = data['results']['artistmatches']['artist'];

        this.setState({
            artistList: artists
        });
       $('li').click(function(e) {
        var clickedArtist = $(this).text();
        console.log(clickedArtist);
        $.ajax({
            url: "http://ws.audioscrobbler.com/2.0/",
            type: 'POST',
            contentType: "application/json; charset=utf-8",
            data: {
              method: 'artist.getInfo',
              artist: clickedArtist,
              api_key : '278b674c37ebf25ee302d1bc7edb0b76',
              format : 'json'
            },
            cache: false,
            success: function(data) {
                console.log(data['artist']['bio']['content']);
                $('#artistbio').html(data['artist']['bio']['content']);
                handleOpenModal();

            }
        });
    });
      }.bind(this),
      // Fail..
      error: function(xhr, status, err) {
        console.log(xhr, status);
        console.log(err);

      }.bind(this)
    });


}

  render() {
    function handleCheck(e){
       // console.log(e.target);
    }
    const items = this.state.artistList.map((item, index) =>
    <li key={index}>{item.name}</li>
    );
    return (
    <div>

      <div className="artists" id="artistList">
        <div className="filter">
        <form id="searchartists" onSubmit={this._handleSubmit}>
          <input type="text" placeholder="Search Artist" name="artist" id='artist' onChange={this._handleChange} />
          <button type='submit'>Go</button>
        </form>
        </div>

        <ul id="results">{items}</ul>
        <p id="artistbio"></p>
        <ReactModal 
           isOpen={this.state.showModal}
           contentLabel="Minimal Modal Example"
        ></ReactModal>
      </div>


</div>
    )
  }

}

export default ArtistList;
